import re

from rss.templates.rss import urlmarker


def linkurls(body):
    urls = re.findall(urlmarker.WEB_URL_REGEX, body)
    urls = set(urls)
    for url in urls:
        body = body.replace(url, '<a href="{}">{}</a>'.format(url, url))
    return body


